/**
 * @param {number[]} arr
 * @param {number} target
 * @return {number}
 */
var threeSumMulti = function(arr, target) {
  let sortedArray = arr.sort((a, b) => a - b);
  let hash = {};
  let record = {};
  let result = 0;
  for (let i = 0; i < sortedArray.length; ++i) {
    if (!hash[sortedArray[i]]) {
      hash[sortedArray[i]] = 1;
    } else {
      hash[sortedArray[i]] += 1;
    }
  }
  for (let i = 0; i < sortedArray.length; ++i) {
    if (!record[sortedArray[i]]) {
      record[sortedArray[i]] = true;
    } else {
      continue;
    }
    let remain = target - sortedArray[i];
    let remainArray = sortedArray.slice(i);
    let left = 0;
    let right = remainArray.length - 1;
    let lastLeft = undefined;
    let lastRight = undefined;
    while (left < right) {
      if (lastLeft === remainArray[left]) {
        left += 1;
        continue;
      }
      if (lastRight === remainArray[right]) {
        right -= 1;
        continue;
      }
      if (remainArray[left] + remainArray[right] > remain) {
        lastRight = remainArray[right];
        right -= 1;
      } else if (remainArray[left] + remainArray[right] < remain) {
        lastLeft = remainArray[left];
        left += 1;
      } else {
        lastRight = remainArray[right];
        lastLeft = remainArray[left];
        let subHash = {};
        addKeyToHashTable(subHash, sortedArray[i]);
        addKeyToHashTable(subHash, remainArray[left]);
        addKeyToHashTable(subHash,remainArray[right]);

        let subResult = 1;
        Object.entries(subHash).forEach(([key, count]) => {
          subResult = subResult * getCombination(hash[key], count);
        });

        subHash = {};
        result += subResult;
        right -= 1;
        left += 1;
      }
    }
  }
  return result % (10**9 + 7);
};

function addKeyToHashTable(hash, key) {
  if (!hash[key]) {
    hash[key] = 1;
  } else {
    hash[key] += 1;
  }
}

function getCombination(n, r) {
  let numerator = n;
  let denumerator = 1;
  for (let i = 1; i < r; ++i) {
    numerator = numerator * (n - i);
    denumerator = denumerator * (i + 1);
  }
  return numerator / denumerator;
}

